mapper.select:
SELECT id, bid, original_file_name, FORMAT(ROUND(file_size / 1024), 0) AS file_size
FROM files
WHERE bid = #{bid}

mapper.update:
UPDATE board
SET view_cnt = view_cnt+1
WHERE id = #{id}

mapper.select:
SELECT id, title, content, view_cnt, writer, created_datetime
FROM board
WHERE id = #{id}

mapper.update:
UPDATE board
SET content = #{content}
WHERE id = #{id}
